mapper.select:
select id, blog_type, see_num, comment_num, praise_num, blog_keyword, is_usable, create_time, create_user_id, update_time, update_user_id, blog_title, blog_abstract from blog where is_usable = 1
and blog_keyword like concat('%',#{blogKeyword},'%')
and blog_type = #{blogType}
and blog_title like concat('%',#{blogTitle},'%')
order by create_time desc limit #{page},#{pageSize}

mapper.update:
 update blog
set is_usable = 0,
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_user_id = #{updateUserId,jdbcType=INTEGER},
  update_user_name = #{updateUserName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
